
public class Control2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=5;
        while(i>=0) {
        	System.out.println(i);
        	i--;
        }
        
        int j=1;
        while(j<=5) {
        	System.out.println(j);
        	j++;
        }
	}

}
