
public class Control3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int j=5;
      do {
    	  System.out.println(j);
    	  j--;
      }while(j>=0);
      
      
      int i = 0;
  	do {
  		System.out.println("Do Block: " + i);
  		i++;
  	} while (i<=5);
  	
  	for(int k=0; k>2; k++) {
		k--;
		System.out.println(k);
	}
  
  	
	}
	
}